SELECT (p.id, p.type_id, p.amount, p.payment_date, p.student_id) FROM payment p INNER JOIN paymenttype pt ON p.type_id = pt.id WHERE pt.name = 'MONTHLY';
SELECT (m.id, m.student_id, m.subject_id, m.mark) FROM mark AS m INNER JOIN subject AS s ON m.subject_id = s.id WHERE s.name = 'Art';
SELECT DISTINCT (s.id, s.name, s.birthday, s.groupnumber) FROM student AS s INNER JOIN payment AS p ON s.id = p.student_id INNER JOIN paymenttype AS pt ON p.type_id = pt.id WHERE pt.name = 'WEEKLY';
SELECT (s.id, s.name, s.birthday, s.groupnumber) FROM student AS s INNER JOIN mark AS m ON s.id = m.student_id INNER JOIN subject AS sbj ON m.subject_id = sbj.id WHERE sbj.name = 'Math';